mapper.select:
SELECT user.`name` AS userName,menu.`food_name` AS foodName,user_menu.`update_time` AS updateTime,user_menu.`data_day` AS dataDay ,menu.`image`,menu.price,menu_type.name AS typeName FROM user_menu INNER JOIN `user` ON user.`id` = user_menu.`user_id` INNER JOIN menu ON menu.id =user_menu.`menu_id` INNER JOIN menu_type ON menu.flag = menu_type.id
WHERE user.`id`=#{id}
ORDER BY updateTime DESC
